float median3(float a, float b, float c) {
  return max(min(a,b), min(max(a,b), c));
}
#define CC_SURFACES_FRAGMENT_MODIFY_BASECOLOR_AND_TRANSPARENCY
vec4 SurfacesFragmentModifyBaseColorAndTransparency() {
  vec3  ms  = texture2D(mainTexture, v_uv).rgb;
  float m   = median3(ms.r, ms.g, ms.b);
  float sd  = m - 0.5;
  float afw = max(fwidth(m), 1e-6);
  float a   = clamp(sd * (pxRange * max(softness, 1e-3)) / afw + 0.5, 0.0, 1.0);
  if (a < alphaClip) discard;
  return vec4(tintColor.rgb, tintColor.a * a);
}
